// src/components/BuildingList.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchBuildings } from '../api';
import { Button, Card, CardContent, Typography } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';

function BuildingList() {
  const [buildings, setBuildings] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getBuildings = async () => {
      try {
        const data = await fetchBuildings();
        setBuildings(data);
      } catch (err) {
        setError('Failed to fetch buildings');
      }
    };
    getBuildings();
  }, []);

  if (error) return <p>{error}</p>;

  return (
    <div>
      <Typography variant="h4" gutterBottom>Buildings</Typography>
      {buildings.map((building) => (
        <Card key={building._id} variant="outlined" style={{ marginBottom: '10px' }}>
          <CardContent>
            <Typography variant="h6">
              <HomeIcon style={{ marginRight: '5px' }} />
              {building.name}
            </Typography>
            <Typography variant="body2">Location: {building.location}</Typography>
            <Typography variant="body2">Target Temperature: {building.targetTemperature || 'N/A'}Â°C</Typography>
            <Button component={Link} to={`/buildings/${building._id}`} variant="contained" color="primary">
              View Details
            </Button>
          </CardContent>
        </Card>
      ))}
      {/* Ensure only one button exists */}
      <Button
        component={Link}
        to="/add-building"
        variant="outlined"
        color="secondary"
        style={{ marginTop: '15px' }}
      >
        Add New Building
      </Button>
    </div>
  );
}

export default BuildingList;
