// src/components/BuildingList.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchBuildings } from '../api';
import './BuildingList.css'; // Import a CSS file for custom styles 

function BuildingList() {
  const [buildings, setBuildings] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getBuildings = async () => {
      try {
        const data = await fetchBuildings();
        setBuildings(data);
      } catch (err) {
        setError('Failed to fetch buildings');
      }
    };
    getBuildings();
  }, []);

  if (error) return <p className="error-message">{error}</p>;

  return (
    <div className="building-list-container">
      <h1 className="title">Buildings</h1>
      <div className="building-cards">
        {buildings.map((building) => (
          <div key={building._id} className="building-card">
            <h2>{building.name}</h2>
            <p><strong>Location:</strong> {building.location}</p>
            <p><strong>Target Temperature:</strong> {building.targetTemperature || 'N/A'}Â°C</p>
            <Link to={`/buildings/${building._id}`} className="view-details-button">View Details</Link>
          </div>
        ))}
      </div>
      //<Link to="/add-building" className="add-building-button">Add New Building</Link>
      <Link
  to="/add-building"
  style={{
    display: 'inline-block',
    padding: '8px 16px',
    color: '#333',
    backgroundColor: '#f8f8f8',
    border: '1px solid #333',
    borderRadius: '4px',
    textDecoration: 'none',
    fontSize: '1em',
    cursor: 'pointer'
  }}
>
  Add New Building
</Link>
    </div>
  );
}

export default BuildingList;
