import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { createBuilding, updateBuilding, fetchBuildingById } from '../api';

function BuildingForm() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [targetTemperature, setTargetTemperature] = useState(22); // Default temperature
  const [error, setError] = useState(null);
  const isEditing = Boolean(id);

  useEffect(() => {
    if (isEditing) {
      const getBuilding = async () => {
        try {
          const data = await fetchBuildingById(id);
          setName(data.name);
          setLocation(data.location);
          setTargetTemperature(data.targetTemperature || 22); 
        } catch (err) {
          setError('Failed to load building details');
        }
      };
      getBuilding();
    }
  }, [id, isEditing]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const buildingData = { name, location, targetTemperature };

    try {
      if (isEditing) {
        await updateBuilding(id, buildingData);
      } else {
        await createBuilding(buildingData);
      }
      navigate('/');
    } catch (err) {
      setError('Failed to save building');
    }
  };

  return (
    <div>
      <h1>{isEditing ? 'Edit Building' : 'Add Building'}</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Building Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Building Name"
            required
          />
        </div>

        <div>
          <label>Location:</label>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="Location"
            required
          />
        </div>

        <div>
          <label>Target Temperature:</label>
          <input
            type="number"
            min="0"
            max="30"
            value={targetTemperature}
            onChange={(e) => setTargetTemperature(e.target.value)}
            placeholder="Target Temperature"
            required
          />
        </div>

        {/* Optional: Use a range slider for a more user-friendly temperature control */}
        <div style={{ marginTop: '10px' }}>
          <label>Adjust Temperature:</label>
          <input
            type="range"
            min="0"
            max="30"
            value={targetTemperature}
            onChange={(e) => setTargetTemperature(e.target.value)}
          />
          <span> {targetTemperature}Â°C</span>
        </div>

        <button type="submit">{isEditing ? 'Update' : 'Add'} Building</button>
      </form>
    </div>
  );
}

export default BuildingForm;
